mapper.select:

 SELECT ta.*
 FROM (
     SELECT /* + index_desc(b pk_product) */ ROWNUM rn, p.*
     FROM (SELECT *
           FROM tbl_product
           ORDER BY
           	  CASE WHEN #{sort} = 'price' THEN product_price END ASC,
         	  CASE WHEN #{sort} = 'createdDate' THEN product_no END DESC
			) p
     ) ta
     WHERE ta.rn <= #{startRow} + #{pageSize}
     AND ta.rn > #{startRow}
